(20, 2, '2022-06-23 17:54:11', '2022-06-23 14:54:11', '<!-- wp:image {\"id\":50,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"https://trifonov-m4.xn--80ahdri7a.site/wp-content/uploads/2022/06/вертикальная-фотшграфия-на-главную-страницу2-1.jpg\" alt=\"\" class=\"wp-image-50\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Брусчатка монетного двора</p><cite>С советских времён государство решалось начать реконструкцию брусчатки площади при монетном дворе, и честь ее заниматься выпал нашей компании и уже к августу 2023 года мы начнём усиливать фундамент и стараться восстановить его в первозданный вид</cite></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Монетный двор', '', 'publish', 'open', 'open', '', '%d0%bd%d0%be%d0%b2%d0%b0%d0%be%d1%81%d1%82%d1%8c-5', '', '', '2022-06-23 22:35:27', '2022-06-23 19:35:27', '', 0, 'https://trifonov-m4.xn--80ahdri7a.site/?p=20', 0, 'post', '', 0),
