(111, 2, '2022-06-27 15:14:16', '2022-06-27 12:14:16', '<!-- wp:metaslider/slider {\"slideshowId\":75} -->\n<div class=\"alignnormal\">[metaslider id=75 cssclass=\"\"]</div>\n<!-- /wp:metaslider/slider -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">О нашей организации</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Организация занимается различной архитектурной работой, по гос. контрактами, внутригородской архитектурой архитектуре и жилим кварталам</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":155,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://trifonov-m4.xn--80ahdri7a.site/wp-content/uploads/2022/06/photo_2022-06-22_22-00-54-1-1024x768.jpg\" alt=\"\" class=\"wp-image-155\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:spacer {\"height\":\"41px\"} -->\n<div style=\"height:41px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Заявка на проблему города</h2>\n<!-- /wp:heading -->\n\n<!-- wp:shortcode -->\n<!--?php\n// @azazaza08\n/*\n * ProblemCitytForm.\n *\n * Plugin Name: ProblemCitytForm\n * Plugin URI: https://trifonov-m4.сделай.site/\n * Description: This form is used to send the problems of the city to the site for their further restoration\n * Version: 1.0\n * Author: Maksim Trifonov\n * Author URI:  https://trifonov-m4.сделай.site/\n * License: GNU GPL v3.0\n * Copyright: 2022 - Maksim Trifonov\n\n */\nadd_shortcode(\'ProblemCitytForm\', \'adв_formы\');\n\nfunction ad(){\n	add_action(\'wp_footer\', \'ad_form\');\n	}\n	\n\nfunction ad_form(){\n	$file=plugin_dir_url(file) . \'add_question.php\';\n	$content=\"<p-->\n<fieldset>\n<form action=\"{$file}\" class=\"form_flex\" method=\"POST\">\n\n<p class=\"width_90 font_small right\">Выберите фото:</p>\n\n<input type=\"file\" name=\"file_foto\">\n\n<p class=\"width_90 font_smal